CREATE TABLE "users" (
  "_id" uuid PRIMARY KEY,
  "name" varchar NOT NULL,
  "email" varchar NOT NULL,
  "password" varchar NOT NULL,
  "role" varchar NOT NULL
);

CREATE TABLE "ingredients" (
  "_id" uuid PRIMARY KEY,
  "name" varchar UNIQUE NOT NULL,
  "type" varchar NOT NULL
);

CREATE TABLE "items" (
  "_id" uuid PRIMARY KEY,
  "name" varchar UNIQUE NOT NULL,
  "type" varchar NOT NULL
);

CREATE TABLE "item_ingredients" (
  "menu_id" uuid,
  "item_id" uuid,
  "type" varchar
);

ALTER TABLE "item_ingredients" ADD FOREIGN KEY ("menu_id") REFERENCES "items" ("_id");

ALTER TABLE "item_ingredients" ADD FOREIGN KEY ("item_id") REFERENCES "ingredients" ("_id");



